mapper.insert:
{
    call dbo.SVC_in_CustomerResponse(
        #{customerCode},
        #{responseType},
        #{responseTitle},
        #{responseContents},
        #{loginId},
        #{successYN, mode=OUT, jdbcType=CHAR, javaType=String},
        #{errorMessage, mode=OUT, jdbcType=NVARCHAR, javaType=String}
    )
    }

mapper.update:
{
    call dbo.SVC_un_CustomerResponse(
        #{crId},
        #{customerCode},
        #{responseType},
        #{responseTitle},
        #{responseContents},
        #{useYN},
        #{loginId},
        #{successYN ,mode=OUT, jdbcType= CHAR, javaType= String},
        #{errorMessage, mode=OUT, jdbcType=NVARCHAR, javaType=String}
    )
    }
