mapper.insert:
{
    call dbo.SVC_in_ResponseResult(
        #{crId},
        #{resultContents},
        #{loginId},
        #{successYN, mode=OUT, jdbcType=CHAR, javaType=String},
        #{errorMessage, mode=OUT,jdbcType=NVARCHAR, javaType=String}
    )
    }

mapper.update:
{
    call dbo.SVC_un_ResponseResult(
        #{rrId},
        #{resultContents},
        #{loginId},
        #{successYN, mode=OUT, jdbcType=CHAR, javaType= String},
        #{errorMessage,mode= OUT, jdbcType=NVARCHAR, javaType=String}
    )
    }

{
    call dbo.SVC_un_ResponseResultYN(
        #{rrId},
        #{useYN},
        #{loginId},
        #{successYN, mode=OUT, jdbcType=CHAR, javaType= String},
        #{errorMessage,mode= OUT, jdbcType=NVARCHAR, javaType=String}
    )
    }
