mapper.insert:
{
    call dbo.CREATE_USER(
        #{userLoginId},
        #{userName},
        #{nickname},
        #{emailAddress},
        #{userPwd},
        #{phoneNumber},
        #{homeAddress},
        #{return_code, mode=OUT, jdbcType=CHAR, javaType=String},
        #{errorMessage, mode=OUT, jdbcType=NVARCHAR, javaType=String}
    )
    }

mapper.select:
{
    call dbo.CHECK_LOGIN(
        #{userLoginId},
        #{userPwd},
        #{resultCode, mode = OUT, jdbcType= CHAR}
    )
    }
